소스코드 <pre><code>FUNCTION         FUN_CREATE_APPLICATOR
RETURN VARCHAR2
IS

V_RTN      VARCHAR2(20);
V_Y        VARCHAR2(1);
V_M        VARCHAR2(1);
V_D        VARCHAR2(1);
V_SEQ      VARCHAR2(6);

BEGIN

  SELECT CVALUE
    INTO  V_Y
    FROM TM_COMMCODE
   WHERE COMMGRP = 'CG005'
     AND COMMNAME = TO_CHAR(SYSDATE,'YYYY');

  SELECT CVALUE
    INTO  V_M
    FROM TM_COMMCODE
   WHERE COMMGRP = 'CG006'
     AND COMMNAME = TO_CHAR(SYSDATE,'MM');

  SELECT CVALUE 
    INTO  V_D
    FROM TM_COMMCODE
   WHERE COMMGRP = 'CG007'
     AND COMMNAME = TO_CHAR(SYSDATE,'DD');

  SELECT LPAD(SEQ_APPLICATOR.NEXTVAL, 6,'0') 
    INTO V_SEQ
    FROM DUAL;

  V_RTN := 'APP'||V_Y||V_M||V_D||V_SEQ;

  RETURN V_RTN;

EXCEPTION

    WHEN OTHERS THEN
        RETURN NULL;

END FUN_CREATE_APPLICATOR;
</code></pre>

