소스코드 <pre><code>FUNCTION         FUN_CREATE_MFG
RETURN VARCHAR2
IS

V_RTN      VARCHAR2(11);
V_Y        VARCHAR2(1);
V_M        VARCHAR2(1);
V_D        VARCHAR2(1);


BEGIN

  SELECT T1.COMMNAME
    INTO  V_Y
    FROM TM_COMMCODE T1
   WHERE COMMGRP = 'CG013'
     AND T1.CVALUE = TO_CHAR(SYSDATE,'YYYY');

  SELECT T1.COMMNAME
    INTO  V_M
    FROM TM_COMMCODE T1
   WHERE COMMGRP = 'CG014'
     AND T1.CVALUE = LPAD(TO_CHAR(SYSDATE,'MM'), 2, '0');

  SELECT T1.COMMNAME 
    INTO  V_D
    FROM TM_COMMCODE T1
